import React, { useState } from 'react';
import die from '../../public/die-face-one.png';

function GameMode() {
	const [playerScore, setPlayerScore] = useState(0);
	const [cpuScore, setCpuScore] = useState(0);
	const [activePlayer, setActivePlayer] = useState("PLAYER");
	const [round, setRound] = useState(1);

	return (
		<div className="GameMode">
			<div col="2">
				<label>Turn { round }</label>
				// <img src="../images/die-face-one.png" />
				// <img src="../images/die-face-two.png" />
				// <img src="../images/die-face-three.png" />
				// <img src="../images/die-face-four.png" />
				// <img src="../images/die-face-five.png" />
				// <img src="../images/die-face-six.png" />
				<img src={die} />

				<button>Roll Dice</button>
			</div>
			<div col="2">
				<label>Player 1</label>
				<h5>Score: { playerScore }</h5>
				<br />
				<label>CPU</label>
				<h5>Score: { cpuScore }</h5>
			</div>

		</div>
	);
}

export default GameMode;
