import React, { useState } from 'react';
import face_one from '../images/die-face-one.png';
import face_two from '../images/die-face-two.png';
import face_three from '../images/die-face-three.png';
import face_four from '../images/die-face-four.png';
import face_five from '../images/die-face-five.png';
import face_six from '../images/die-face-six.png';

function GameMode() {
	const [playerScore, setPlayerScore] = useState(0);
	const [cpuScore, setCpuScore] = useState(0);
	const [activePlayer, setActivePlayer] = useState("PLAYER");
	const [round, setRound] = useState(1);

	return (
		<div className="GameMode">
			<div col="2">
				<label style={{color: "red"}}>Turn { round }</label>
				<img style={{ height: 30 }} src={ face_one } />
				<img style={{ height: 30 }} src={ face_two } />
				<img style={{ height: 30 }} src={ face_three } />
				<img style={{ height: 30 }} src={ face_four } />
				<img style={{ height: 30 }} src={ face_five } />
				<img style={{ height: 30 }} src={ face_six } />

				<button>Roll Dice</button>
			</div>
			<div col="2">
				<label>Player 1</label>
				<h5>Score: { playerScore }</h5>
				<br />
				<label>CPU</label>
				<h5>Score: { cpuScore }</h5>
			</div>
		</div>
	);
}

export default GameMode;
