import React, { useState } from 'react';
import face_one from '../images/die-face-one.png';
import face_two from '../images/die-face-two.png';
import face_three from '../images/die-face-three.png';
import face_four from '../images/die-face-four.png';
import face_five from '../images/die-face-five.png';
import face_six from '../images/die-face-six.png';

function DiceCollection({ startGame }) {

	const die_faces = {
		one: face_one,
		two: face_two,
		three: face_three,
		four: face_four,
		five: face_five,
		six: face_six
	}

	const rollDice = () => {
		let first_die = Math.floor(Math.random() * 6) + 1;
		let second_die = Math.floor(Math.random() * 6) + 1;
		let third_die = Math.floor(Math.random() * 6) + 1;
		let fourth_die = Math.floor(Math.random() * 6) + 1;
		let fifth_die = Math.floor(Math.random() * 6) + 1;
		let sixth_die = Math.floor(Math.random() * 6) + 1;

		if(first_die == 1) {
			die_faces.one = face_one
		}
		else if(first_die == 2) {
			die_faces.one = face_two
		}
		else if(first_die == 3) {
			die_faces.one = face_three
		}
		else if(first_die == 4) {
			die_faces.one = face_four
		}
		else if(first_die == 5) {
			die_faces.one = face_five
		}
		else if(first_die == 6) {
			die_faces.one = face_six
		}
	}

	return (
		<div className="DiceCollection">
			<img style={{ height: 40 }} src={ die_faces.one } />
			<img style={{ height: 40 }} src={ die_faces.two } />
			<img style={{ height: 40 }} src={ die_faces.three } />
			<img style={{ height: 40 }} src={ die_faces.four } />
			<img style={{ height: 40 }} src={ die_faces.five } />
			<img style={{ height: 40 }} src={ die_faces.six } />

			<button onClick={rollDice}>Roll Dice</button>
		</div>
  );
}

export default DiceCollection;
